import React, { useState } from "react";
import './App.css';
import data from "./data.json";


function App() {

  const [patientInfo, setPatientInfo] = useState(data);
  const [addformData, setaddFormData] = useState({
    firstName: '',
    lastName: '',
    dateOfBirth:'',
    healthCard: '',
    gender: ''
  })

  const handleForm = (event) =>{
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    const newFormData = { ...addformData};
    newFormData[fieldName] = fieldValue;
    setaddFormData(newFormData);
  }

  const handleCardNumber = (event) =>{
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    const newFormData = { ...addformData};
    newFormData[fieldName] = fieldValue;
    setaddFormData(newFormData);
  }

  const handleFormSubmit = (event) => {
    alert('Form was submitted');
    event.preventDefault();

    const newPatient = {
      firstName: addformData.firstName,
      lastName: addformData.lastName,
      dateOfBirth: addformData.dateOfBirth,
      healthCard: addformData.healthCard,
      gender: addformData.gender
    }

    const newPatients = [...patientInfo, newPatient];
    setPatientInfo(newPatients);
  }

  return (
    <div className="app-container">
      <h2>Fill the Form</h2>
      <div id = "addForm">
        <form onSubmit={handleFormSubmit}>
          <div>
            <label for ="firstName">First Name:</label>
            <input type="text" id = "firstName" name = "firstName" maxLength = "10" value = {addformData["firstName"]}  onChange={handleForm} required ="required" />
          </div>

          <div>
            <label for = "lastName">Last Name:</label>
            <input type="text" id ="lastName" name = "lastName" maxLength = "10" value = {addformData["lastName"]}  onChange={handleForm} required ="required" />
          </div>

          <div>
          <label for = "dateOfBirth" >Date of birth:</label>
          <input type="date" name = "dateOfBirth" value = {addformData["dateOfBirth"]}   onChange={handleForm} required ="required" id ="dateOfBirth"/>
          </div>

          <div>
            <label for="healthCard">Health Card Number:</label>
            <input type="text" name = "healthCard" value = {addformData["healthCard"]} maxLength="10" onChange={handleCardNumber} required ="required" id ="healthCard" />
          </div>

          <div>
            <label for ="gender">Gender:</label> 
            <select name = "gender" value = {addformData["gender"]}  onChange={handleForm} id ="gender">
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
              <option value="Did not wanna share">Don't Wanna Share</option>
            </select>
          </div>

          <input type="submit" value="Submit" id = "submit" disabled = "true"/>
        </form>
      </div>
      <h2>Patient Information</h2>
      <table>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Date of birth</th>
            <th>HealthCard Number</th>
            <th>Gender</th>
          </tr>
        </thead>
        <tbody>
          {patientInfo.map((patient)=>
          <tr>
            <td>{patient.firstName}</td>
            <td>{patient.lastName}</td>
            <td>{patient.dateOfBirth}</td>
            <td>{patient.healthCard}</td>
            <td>{patient.gender}</td>
        </tr>
        )}   
        </tbody>
      </table>
      <footer>
        <></>
      </footer>
    </div>

  );
}

export default App;
