import React, { useState } from "react";
import './App.css';
import data from "./data.json";
import Greet from './components/Greet'
import Table from './components/Table'
import DataFetching from './components/DataFetching';


function App() {

  const [patientInfo, setPatientInfo] = useState(data);
  const [addformData, setaddFormData] = useState({
    firstName: '',
    lastName: '',
    dateOfBirth:'',
    healthCard: '',
    gender: ''
  })

  const handleForm = (event) =>{
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    const newFormData = { ...addformData};
    newFormData[fieldName] = fieldValue;
    setaddFormData(newFormData);
  }

  const handleCardNumber = (event) =>{
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    const newFormData = { ...addformData};
    newFormData[fieldName] = fieldValue;
    setaddFormData(newFormData);
  }

  const handleFormSubmit = (event) => {
    alert('Form was submitted');
    event.preventDefault();

    const newPatient = {
      firstName: addformData.firstName,
      lastName: addformData.lastName,
      dateOfBirth: addformData.dateOfBirth,
      healthCard: addformData.healthCard,
      gender: addformData.gender
    }

    const newPatients = [...patientInfo, newPatient];
    setPatientInfo(newPatients);
  }

  return (
    <div className="app-container">
      <h2>Fill the Form</h2>
      <form onSubmit={handleFormSubmit}>
        <label>
          First Name:
          <input type="text" name = "firstName" maxLength = "10" value = {addformData["firstName"]}  onChange={handleForm} required ="required" />
        </label>
        <label>
          Last Name:
          <input type="text" name = "lastName" maxLength = "10" value = {addformData["lastName"]}  onChange={handleForm} required ="required" />
        </label>
        <label>
          Date of birth:
          <input type="date" name = "dateOfBirth" value = {addformData["dateOfBirth"]}   onChange={handleForm} required ="required"/>
        </label>
        <label>
          Health Card Number:
          <input type="text" name = "healthCard" value = {addformData["healthCard"]} maxLength="10" onChange={handleCardNumber} required ="required"/>
        </label>
        <label>
          Gender:
          <select name = "gender" value = {addformData["gender"]}  onChange={handleForm}>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
            <option value="Did not wanna share">Don't Wanna Share</option>
          </select>
        </label>
        <input type="submit" value="Submit" />
      </form>
      <h2>Patient Information</h2>
      <table>
        <thread>
          <tr>
            <th>First Name</th>
            <th>Second Name</th>
            <th>Date of birth</th>
            <th>HealthCard Number</th>
            <th>Gender</th>
          </tr>
        </thread>
        <tbody>
          {patientInfo.map((patient)=>
          <tr>
            <td>{patient.firstName}</td>
            <td>{patient.lastName}</td>
            <td>{patient.dateOfBirth}</td>
            <td>{patient.healthCard}</td>
            <td>{patient.gender}</td>
        </tr>
        )}   
        </tbody>
      </table>
    </div>
  );
}

export default App;
