import React, { useState } from "react";
import './App.css';
import data from "./data.json";
import Greet from './components/Greet'
import Table from './components/Table'
import DataFetching from './components/DataFetching';


function App() {

  const [patientInfo, setPatientInfo] = useState(data);
  const [addformData, setaddFormData] = useState({
    firstName: '',
    lastName: '',
    dateOfBirth:'',
    healthCard: '',
    gender: ''
  })

  const handleFirstName = (event) =>{
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    if(fieldName.length < 5){
      const newFormData = { ...addformData};
      newFormData[fieldName] = fieldValue;
      setaddFormData(newFormData);
    }
  }
  const handleLastName = (event) =>{
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    const newFormData = { ...addformData};
    newFormData[fieldName] = fieldValue;
    setaddFormData(newFormData);
  }
  const handleDateOfBirth = (event) =>{
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    const newFormData = { ...addformData};
    newFormData[fieldName] = fieldValue;
    setaddFormData(newFormData);
  }
  const handleCardNumber = (event) =>{
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    const newFormData = { ...addformData};
    newFormData[fieldName] = fieldValue;
    setaddFormData(newFormData);
  }
  const handleGender = (event) =>{
    event.preventDefault();
    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    const newFormData = { ...addformData};
    newFormData[fieldName] = fieldValue;
    setaddFormData(newFormData);
  }

  return (
    <div className="App">
      <h2>Fill the Form</h2>
      <form>
        <label>
          First Name:
          <input type="text" name = "firstName" value = {addformData["firstName"]} maxLength="10" onChange={handleFirstName} required ="required" />
        </label>
        <label>
          Last Name:
          <input type="text" name = "lastName" onChange={handleLastName} required ="required" />
        </label>
        <label>
          Date of birth:
          <input type="date" name = "dateOfBirth"  onChange={handleDateOfBirth} required ="required"/>
        </label>
        <label>
          Health Card Number:
          <input type="text" name = "healthCard" maxLength="10" onChange={handleCardNumber} required ="required"/>
        </label>
        <label>
          Gender:
          <select name = "gender" onChange={handleGender}>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
            <option value="Did not wanna share">Don't Wanna Share</option>
          </select>
        </label>
        <input type="submit" value="Submit" />
      </form>
      <h2>Patient Information</h2>
      <table>
        <thread>
          <tr>
            <th>First Name</th>
            <th>Second Name</th>
            <th>Date of birth</th>
            <th>HealthCard Number</th>
            <th>Gender</th>
          </tr>
        </thread>
        <tbody>
          {patientInfo.map((patient)=>
          <tr>
            <td>{patient.firstName}</td>
            <td>{patient.lastName}</td>
            <td>{patient.dateOfBirth}</td>
            <td>{patient.healthCard}</td>
            <td>{patient.gender}</td>
        </tr>
        )}   
        </tbody>
      </table>
    </div>
  );
}

export default App;
